import { ADD_CHARACTER, REMOVE_CHARACTER, FILTER } from "./types";


const initialState = {
    myFavorites: [],
    allCharacters: []
}



const rootReducer = (state = initialState, { type, payload }) => {
    switch (type) {
        case ADD_CHARACTER:
            console.log(payload);
            return { ...state, myFavorites: [...state.myFavorites, payload] }
        case REMOVE_CHARACTER:
            return { ...state, myFavorites: state.myFavorites.filter((idx) => idx.detailId !== payload.detailId) }
        default:
            return { ...state }
    }

}

export default rootReducer;
