import { ADD_CHARACTER,REMOVE_CHARACTER } from "./types";


const initialState = {
    myFavorites: []
}

const rootReducer = (state = initialState, action)=>{
  
    
            
    switch (action.type) {
        case ADD_CHARACTER:
            return{
                ...state,
                myFavorites:[...state.myFavorites, action.payload]
            }
        case REMOVE_CHARACTER:
            const filteredList = state.myFavorites.filter(el=>el.id !== action.payload);
            return{
                ...state,
                myFavorites:filteredList 
            }
        default:{
            return {...state}
            // return[...state.myFavorites];
        }

    }
}

export default rootReducer;
