import { addCharacter, removeCharacter } from "./actions";
import { ADD_CHARACTER,REMOVE_CHARACTER } from "./types";


const initialState = {
    myFavorites: []
}

const rootReducer = (state = initialState, {type,payload})=>{
  
    
            
    switch (type) {
        case ADD_CHARACTER:
            return{
                ...state,
                myFavorites:[...state.myFavorites, payload]
            }
        case REMOVE_CHARACTER:
            const filteredList = state.myFavorites.filter(el=>el.id !== payload);
            return{
                ...state,
                myFavorites:filteredList 
            }
        default:{
            return {...state}
            // return[...state.myFavorites];
        }

    }
}

export default rootReducer;
