import './App.css'
import Nav from './components/Nav'
import Cards from './components/Cards.jsx'
import React from 'react'
let arr = [];
function App() {
  
  const [characters, setCharacters] = React.useState([]);
  


  async function onSearch(character) {

    

    await fetch(`https://rickandmortyapi.com/api/character/${character}`)
      .then((response) => response.json())
      .then((data) => {  
  
        if(arr.includes(character)) throw TypeError;
        if (data.name) {

          setCharacters((oldChars) => [...oldChars, data]);
        } else {
          throw TypeError  
        }
        
      })
      .catch((err) =>{
        window.alert(`ID not found: "${character}"`)
      });      
  }

  function random(){
    let result = Math.floor(Math.random()*826);
    
    arr.push(result);

    console.log(arr);
    onSearch(result);

  }


  function onClose(id){
    setCharacters(characters.filter(character => character.id !== id));
  }

  return (
    <div className='App'>
      <div>
        <Nav onSearch={onSearch} random={random} />
      </div>
      <div>
        <Cards characters={characters} onClose={onClose}/>
      </div>
    </div>
  )
}

export default App
