import { addCharacter, removeCharacter } from "./actions";
import { ADD_CHARACTER,REMOVE_CHARACTER } from "./types";


const initialState = {
    myFavorites: [],
    id: ""
}

const rootReducer = (state = initialState, action)=>{
  
    
            
    switch (action.type) {
        case ADD_CHARACTER:
            const update = [...myFavorites, action.payload]
            return{
                ...state,
                myFavorites: update
            }
        case REMOVE_CHARACTER:
            const filteredList = state.myFavorites.filter(char => id !== action.payload);
            return{
                ...state,
                myFavorites: filteredList
            }
        
        default:{
            return state
            // return[...state.myFavorites];
        }

    }
}

export default rootReducer;
