import styles from "./Card.module.css";
import {Link} from "react-router-dom";
import {addCharacter, removeCharacter} from "../redux/actions"
import { connect } from "react-redux";
import { useState, useEffect } from "react";


function Card(props) {


  const [isFav,setIsFav] = useState(false);
  

  useEffect(() => {
    props.myFavorites
    props.myFavorites.forEach((fav) => {
       if (fav.id === props.id) {
          setIsFav(true);
       }
    });
  }, [props.myFavorites]);;

  

  function handleFavorite() {
    if(isFav){
      setIsFav(false)
      props.removeCharacter(props)
    }else{
      setIsFav(true)
      props.addCharacter(props.detailId)
    }
 }

  return (
    <div className={styles.Card}>
      <Link to={`/detail/${props.detailId}`}>
        <img src={props.image} alt={props.name} />
        <h2>{`Name: ${props.name}`}</h2>
        <h2>{`Species: ${props.species}`}</h2>
        <h2>{`Gender: ${props.gender}`}</h2>
      </Link>
      {isFav ? (
        <button onClick={handleFavorite}>‚ù§Ô∏è</button>
      ) : (
        <button onClick={handleFavorite}>ü§ç</button>
      )}
      <button onClick={props.onClose}>X</button>
    </div>
  );


  


}


const mapDispatchToProps= (dispatch)=> {
  return {
    addCharacter :function(id){ dispatch(addCharacter(id))},
    removeCharacter:function(id) {dispatch(removeCharacter(id))}
  }
}

const mapStateToProps = (state) => {
  return {
    myFavorites : state.myFavorites
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(Card);
