import './App.css'
import Nav from './components/Nav'
import Cards from './components/Cards.jsx'
import React from 'react'

function App() {
  
  const [characters,setCharacters] = React.useState([]);
  
  async function onSearch(input){
    //hacer una busqueda 
    const results= await obtenerDatos();
    
    input = Number(input);
    const respuesta = results.filter(personaje => {
      if(personaje.id === input){
        console.log(personaje);
        return personaje;
      }
        return false;
    });
    console.log(characters);
    setCharacters([...characters,respuesta]);
    //setCharacters([...characters]);  //copide del estado anterior mas un nuevo valor
  }

  React.useEffect(()=>{
    obtenerDatos();
  },[]);


  const obtenerDatos = async ()=> {
    const data = await fetch('https://rickandmortyapi.com/api/character/');
    const {results} = await data.json()
    return results;
  }

  return (
    <div className='App'>
      <div>
        <Nav onSearch={onSearch}/>
      </div> 
      <div>
        <Cards characters={characters}/>
      </div>
    </div>
  )
}

export default App
