import styles from "./Card.module.css";
import {Link} from "react-router-dom";
import {addCharacter, removeCharacter} from "../redux/actions"

function Card(props, {addCharacter, removeCharacter}) {
 
  function handleSubmit(addCharacter, removeCharacter){


  }




  return (
    <div className={styles.Card}>
      <Link to={`/detail/${props.detailId}`}>
        <img src={props.image} alt={props.name} />
        <h2>{`Name: ${props.name}`}</h2>
        <h2>{`Species: ${props.species}`}</h2>
        <h2>{`Gender: ${props.gender}`}</h2>
      </Link>
      <button onClick={props.onClose}>X</button>
    </div>

  );


  


}


const mapDispatchToProps= (dispatch)=> {
  return {
    addCharacter :(id)=> dispatch(addCharacter(id)),
    removeCharacter:(id) => dispatch(removeCharacter(id))
  }
}


export default connect(null, mapDispatchToProps)(Card);
