import React from "react";
import styles from "./Favorites.module.css";
import { NavLink as NavLinkRRD } from "react-router-dom";
import { connect , useSelector} from "react-redux";
import Card from "./Card";

const NavLink = ({ to, children, ...props }) => {
  return (
    <NavLinkRRD
      {...props}
      className={({ isActive }) => (isActive ? styles.isActive : undefined)}
      to={to}
    >
      {children}
    </NavLinkRRD>
  );
};

function Favorites(props) {
  const { allCharacters } = useSelector((state) => state.allCharacters);


  const handleChange = (e)=> {
    console.log("Ejecutando ando...");
  }


  return (
    <div className={styles.Cards}>
      {props.myFavorites.map((el) => (
        <Card
          key={el.detailId}
          detailId={el.detailId}
          name={el.name}
          species={el.species}
          gender={el.gender}
          image={el.image}
        />
      ))}
      <div>
        <select name="Order">
          <option value="Asendente" onClick={handleChange}>Asendente</option>
          <option value="Desendente" onChange={handleChange}>Desendente</option>
        </select>
        <select name="Gender">
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Genderless">Genderless</option>
          <option value="unknown">unknown</option>
        </select>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};
export default connect(mapStateToProps, null)(Favorites);
