import { ADD_CHARACTER,REMOVE_CHARACTER } from "./types";

export const addCharacter = (character)=> {
    return{
        type:ADD_CHARACTER,
        payload: character
    }
}
export const removeCharacter = (id)=> {
    return{
        type:REMOVE_CHARACTER,
        payload: id
    }
}
