import { ADD_CHARACTER,REMOVE_CHARACTER } from "./types";


const initialState = {
    myFavorites: []
}

const rootReducer = (state = initialState, action)=>{
    // switch (action.type) {
    //     case ADD_CHARACTER:
    //         return{
    //             ...state,
    //             myFavorites:[...state.myFavorites, action.payload]
    //         }
    //     case REMOVE_CHARACTER:
    //         return{
    //             ...state,
    //             myFavorites:state.myFavorites.filter(el => el.id !== action.payload)
    //         }
    //     default:{
    //         return {state}
    //         // return[...state.myFavorites];
    //     }

    // }
        console.log(state);
        switch (action.type) {
            case ADD_CHARACTER:
                return {...state, myFavorites: [...state.myFavorites, action.payload]}
            case REMOVE_CHARACTER:
                return {...state, myFavorites: state.myFavorites.filter(idx => idx.id !== action.payload)}
            default:
                return {...state}
        }
}

export default rootReducer;
