export default function Validation({username, password}) {
    const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
    const errors = {};
    
    if(!username) errors.username = "No puede estar vacio";
    if(username.length > 35) errors.username = "No puede tener mas de 35 caracteres";
    if(!regexEmail.test(username)) errors.username = "mal"
    return errors;
  }
